        time(time) {
            let date = new Date(time * 1000);
            let Y = date.getFullYear() + '-';
            let M = (date.getMonth()+1 < 10 ? '0'+(date.getMonth()+1) : date.getMonth()+1) + '-';
            let D = date.getDate() + ' ';
            let h = date.getHours() + ':';
            let m = date.getMinutes();
            let s = date.getSeconds();
            return Y+M+D+h+m
        },
        delHtmlTag(str){
            return str.replace(/<[^>]+>/g,"");  //正则去掉所有的html标记
        }
    }
}
